mapper.select:
SELECT COUNT(*) FROM MEMBER


        AND DEPT_NO = #{deptNo}


        AND POSITION_NO = #{positionNo}


        AND STATUS = #{status}


    	AND USER_NAME LIKE CONCAT(#{name}, '%')

		SELECT *
		FROM (SELECT ROWNUM RNUM, A.*
		      FROM (SELECT USER_NO, USER_NAME, EMAIL, EXTENSION, PHONE, DEPT_NO, DEPT_NAME, POSITION_NO, POSITION_NAME, HIRE_DATE, UPDATE_DATE, TOTAL_LEAVE_DAYS, ADDRESS, ADDRESS_DETAIL
		            FROM MEMBER
                    JOIN DEPARTMENT USING(DEPT_NO)
                    JOIN POSITION USING(POSITION_NO)


	            AND DEPT_NO = #{filter.deptNo}


	            AND POSITION_NO = #{filter.positionNo}


	            AND STATUS = #{filter.status}


	        	AND USER_NAME LIKE CONCAT(#{filter.name}, '%')


		            ORDER BY HIRE_DATE DESC, USER_NO DESC) A)
		WHERE RNUM BETWEEN #{pr.startRow} AND #{pr.endRow}

mapper.update:
UPDATE PROFILE_ATTACHMENT
SET ATTACH_STATUS = 'N'
WHERE REF_USER_NO = #{userNo}
